// Config
var beforeValue = "2013",
    afterValue = "2014";

var margin = {top: 30, right: 10, bottom: 10, left: 10};
    margin.left = 225,
    width = 720 - margin.left - margin.right,
    height = 1000 - margin.top - margin.bottom;

var x = d3.scale.linear()
    .range([0, width])

var y = d3.scale.ordinal()
    .rangeRoundBands([0, height], .2);

var xAxis = d3.svg.axis()
    .scale(x)
    .tickFormat(function(d) {
      return d+"%";
    })
    .orient("top");

var yAxis = d3.svg.axis()
    .scale(y)
    .orient("left");

var tip = d3.tip()
    .attr('class', 'd3-tip')
    .offset([-10, 0])
    .html(function(d) {
    return "<strong>" + d.company + ":</strong> <span style='color:red'>" + Math.round(d.value) + "%" + "</span>";
})

var svg = d3.select("#chart").append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
  .append("g")
    .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

svg.call(tip);

d3.csv("/assets/women-in-tech/data.csv", type, function(error, data) {
    x.domain(d3.extent(data, function(d) { return d.value; })).nice();
    y.domain(data.map(function(d) { return d.company; }));

    svg.selectAll(".bar")
        .data(data)
    .enter().append("rect")
        .attr("class", function(d) { return d.value < 0 ? "bar negative" : "bar positive"; })
        .attr("x", function(d) { return x(Math.min(0, d.value)); })
        .attr("y", function(d) { return y(d.company); })
        .attr("width", function(d) { return Math.abs(x(d.value) - x(0)); })
        .attr("height", y.rangeBand())
        .on('mouseover', tip.show)
        .on('mouseout', tip.hide);

    svg.append("g")
        .attr("class", "x axis")
        .call(xAxis);

    svg.append("g")
        .attr("class", "y axis")
    .append("line")
        .attr("x1", x(0))
        .attr("x2", x(0))
        .attr("y2", height);

    svg.append("g")
        .attr("class", "y axis")
        .call(yAxis)
        .append("text")
            .attr({
                "transform": "rotate(-90)",
                "y": 6,
                "dy": ".71em"
          })
            .style("text-anchor", "end")
            .text("");
});

function type(d) {
    d.value = (parseFloat(d[beforeValue]) - parseFloat(d[afterValue]));
    d.value = d.value/parseFloat(d[beforeValue])*100;
    return d;
}
